  function save(){const u={...form,value:+form.value};if(sel)setLeads(ls=>ls.map(l=>l.id===sel.id?{...u,id:l.id}:l));else setLeads(ls=>[...ls,{...u,id:Date.now()}]);setShowM(false);}
